class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int> f(n);
        for(int i=0;i<n;i++)f[i]=target[i]-nums[i];
        long long ans=0,num=0;
        for(int i=0;i<n;i++){
          if(f[i]>=0){
            if(i==0){
              ans+=f[i];
            }
            if(i>0&&f[i-1]<f[i]){
              ans+=min(f[i]-f[i-1],f[i]);
            }
          }else{
            if(i==0)ans-=f[i];
            if(i>0&&f[i-1]>f[i]){
              ans+=min(f[i-1]-f[i],-f[i]);
            }
          }
        }
        return ans;
    }
};
