class Solution {
public:

    int min_operations(vector<int>& A)  
    {  
        // Base Case  
        if (A.size() == 0)  
            return 0;  

        // Initialize ans to first element  
        int ans = A[0];  

        for (int i = 1; i < A.size(); i++) {  

            // For A[i] > A[i-1], operation  
            // (A[i] - A[i - 1]) is required  
            ans += max(A[i] - A[i - 1], 0);  
        }  

        // Return the answer  
        return ans;  
    } 

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        long long ans=0;
        for(int i=0;i<n;){
            if(nums[i]>=target[i]){
                vector<int> arr;
                while(i<n && nums[i]>=target[i]){
                    arr.push_back(nums[i]-target[i]);
                    i++;
                }
                ans+=min_operations(arr);
            }
            else{
                vector<int> arr;
                while(i<n && nums[i]<target[i]){
                    arr.push_back(target[i]-nums[i]);
                    i++;
                }
                ans+=min_operations(arr);
            }
        }
        return ans;
    }
};
