class Solution {
public:
    long long dp[100010];
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        dp[0]= abs(nums[0]-target[0]);
        for (int i = 1; i < nums.size(); ++i) {
            if ((((long long)nums[i]-target[i]>0)&&(long long)nums[i-1]-target[i-1]>0)||((long long)nums[i]-target[i]<0)&&(long long)nums[i-1]-target[i-1]<0){
               if (abs(nums[i]-target[i])<abs(nums[i-1]-target[i-1]))
                    dp[i]= dp[i-1];
                else{
                    dp[i]= abs(nums[i]-target[i])-abs(nums[i-1]-target[i-1])+dp[i-1];
                }

            }
            else{
                dp[i]=dp[i-1]+abs(nums[i]-target[i]);
            }
         //   cout<<dp[i]<<" ";
        }
        return dp[nums.size()-1];
    }
};
