class Solution {
public:
    #define ll long long
    void AddLayer(ll &ans, int i, int j, vector<ll>&diff, int n)
    {
        // cout<<"i "<<i<<" j "<<j<<endl;
        int mn = diff[i];
        for(int p = i; p <= j; p++)
        {
            if(diff[p] < mn)mn = diff[p];
        }
        ans += mn;
        for(int p = i; p <= j; p++)
        {
            (diff[p]) -= mn;            
        }
        for(int p = i; p <= j; p++)
        {
            if(diff[p] > 0)
            {
                // cout<<"diff p "<<diff[p]<<endl;

                int q = p;
                while(q <= j && diff[q] > 0)q++;
                q--;
                if(p <= q)
                {

                    AddLayer(ans, p, q, diff, n);
                    p = q;
                }

            }
        }


    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll ans = 0;
        ll n = nums.size();
        vector<ll>diff(n);
        map<int, vector<int>>mp;
        for(int i = 0; i < n; i++)
        {
            diff[i] = target[i] - nums[i];
            mp[diff[i]].push_back(i);
            // cout<<diff[i]<<" ";
        }
        cout<<endl;
        int prev = 0;
        for(int i = 0; i < n; i++)
        {
            if(diff[i] > 0)
            {
                int j = i;
                while(j < n && diff[j] > 0)j++;
                j--;
                if(i <= j)
                {                    
                    AddLayer(ans, i, j, diff, n);
                    i = j;
                }                
            }
        }
        for(int i = 0; i < n; i++)diff[i] *= (-1);
        for(int i = 0; i < n; i++)
        {

            if(diff[i] > 0)
            {
                int j = i;
                while(j < n && diff[j] > 0)j++;
                j--;
                if(i <= j)
                {                    
                    AddLayer(ans, i, j, diff, n);
                    i = j;
                }                
            }
        }
        return ans;
    }
};
