class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> diff;
        diff.push_back(0);
        for(int i = 0; i < nums.size(); i++) diff.push_back(nums[i] - target[i]);
        //for(auto i : diff) cout << i << " ";
        int prev = 0;
        long long a = 0;
        for(int i = 1; i < diff.size(); i++){
            if((diff[i] > 0 && diff[i-1] < 0) || (diff[i] < 0 && diff[i-1] > 0)) prev = 0;
            if(abs(diff[i]) > abs(prev)) a += abs(diff[i] - prev);
            prev = diff[i];
        }
        return a;
    }
};
