typedef vector<vector<int>> VVI;
typedef vector<vector<PI>> VVPI;
#define MAX(x) *max_element(ALL(x))
#define MIN(x) *min_element(ALL(x))
#define FOR(i, n) for (int i = 0; i < n; i++) 
#define FOR1(i, n) for (int i = 1; i <= n; i++) 
#define SORT(x) sort(x.begin(), x.end())
#define RSORT(x) sort(x.rbegin(), x.rend())
#define SUM(x) accumulate(x.begin(), x.end(), 0LL)

class Solution {
	LL solve(VL a) {
		LL n = a.size(), i, j, k, minn = MIN(a), ans = 0;;

		set<int> s;
		FOR(i, n) s.insert(i);
		VPL v;
		FOR1(i, n - 2) v.push_back({ a[i], i });
		RSORT(v);

		for (auto p : v) {
			LL id = p.second;
			LL x = p.first;
			s.erase(id);
			set<int>::iterator it = s.upper_bound(id);
			LL r = *it, l;
			it--;
			l = *it;

			LL ll = a[l], rr = a[r];

			if (a[l] > a[r]) ans += x - a[l];
			else ans += x - a[r];
		}

		return ans;
	}
public:
	long long minimumOperations(vector<int>& nums, vector<int>& target) {
		LL n = nums.size(), i, j, k, ans = 0, s = 0, minn, maxx;
		VL v(n + 1, 0);
		FOR(i, n) v[i + 1] = target[i] - nums[i];

		FOR1(i, n) {
			if (v[i] == 0) continue;
			VL b;
			b.push_back(0);
			if (v[i] > 0) {
				for (j = i; (j <= n) && (v[j] > 0); j++) b.push_back(abs(v[j])); 
				i = j - 1;
				b.push_back(0);
				maxx = solve(b);
				ans += maxx;
			}
			else {
				for (j = i; (j <= n) && (v[j] < 0); j++) b.push_back(abs(v[j]));
				i = j - 1;
				b.push_back(0);
				maxx = solve(b);
				ans += maxx;
			}


		}
		return ans;
	}
};
