class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = []
        for i in range(len(nums)):
            diff.append(nums[i]-target[i])
            i+=1

        i = 0
        count = 0
        while i<len(diff):
            if diff[i] < 0:
                j = i
                while j<len(diff) and diff[j] < 0:
                    diff[j] = -diff[j]
                    j+=1
                count += self.ops(diff[i:j])
                i = j
            elif diff[i] > 0:
                j = i
                while j<len(diff) and diff[j] > 0:
                    j+=1
                count += self.ops(diff[i:j])
                i = j
            else:
                i+=1
        return count

    def ops(self,arr):
        if len(arr) == 0:
            return 0
        indices = []
        min_elem = 10**9
        for i in range(len(arr)):
            if arr[i] == min_elem:
                indices.append(i)
            elif arr[i] < min_elem:
                indices = []
                indices.append(i)
                min_elem = arr[i]
        for i in range(len(arr)):
            arr[i] -= min_elem
        i = 0
        ans = min_elem
        for j in indices:
            ans += self.ops(arr[i:j])
            i = j+1
        if i < len(arr):
            ans += self.ops(arr[i:])
        return ans
