class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        res = 0
        p = 0
        for i, x in enumerate(nums):
            d = target[i] - x
            if d > 0:
                if p > 0:
                    if d > p:
                        res += d - p
                else:
                    res += d
            elif d < 0:
                if p < 0:
                    if d < p:
                        res += p - d
                else:
                    res -= d
            p = d
        return res
