typedef long long ll;
typedef long double ld;

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for (int i = 0; i < n; ++i) nums[i] -= target[i];
        vector<int> d(n);
        d[0] = nums[0];
        for (int i = 0; i + 1 < n; ++i) d[i + 1] = nums[i + 1] - nums[i];
        ll cnt_pos = 0, cnt_neg = 0;
        for (int i = 0; i < n; ++i) {
            if (d[i] > 0) cnt_pos += d[i];
            else if (d[i] < 0) cnt_neg -= d[i];
        }
        return max(cnt_pos, cnt_neg);
    }
};
