class Solution:
    def minimumOperations(self, nums: List[int], ta: List[int]) -> int:
        # array, >= 0
        def cal(nums):
            nums = [abs(i) for i in nums]


        nums = [i - j for i, j in zip(nums, ta)]
        # ret = 0
        # cur = []
        # for i in nums:
