        #         ret += cal(cur)
        #         cur = []
        #     cur.append(i)
        # ret += cal(cur)
        # return ret
        print(nums)
        x = y = 0
        n = len(nums)
        for i in range(n - 1):
            c = nums[i + 1] - nums[i]
            if c < 0:
                x -= c
            else:
                y += c
        if nums[0] * nums[-1] <= 0:
            return max(x, y)
        return max(x, y) + min(abs(nums[0]), abs(nums[-1]))
