func absValue(a int) int {
if a < 0 {
return -a
}
return a
}

func minimumOperations(nums []int, target []int) int64 {

length := len(nums)

resultValuesValues := make([]int, length)

for i := 0; i < length; i++ {
resultValuesValues[i] = target[i] - nums[i]
}
resultValues := int64(0)
resultValues += int64(absValue(resultValuesValues[0]))
for i := 1; i < length; i++ {
if resultValuesValues[i]*resultValuesValues[i-1] >= 0 {
if absValue(resultValuesValues[i]) > absValue(resultValuesValues[i-1]) {
resultValues += int64(absValue(resultValuesValues[i-1] - resultValuesValues[i]))
}
} else {
resultValues += int64(absValue(resultValuesValues[i]))
}
}
return resultValues
}
