using ll = long long;

class Solution {
public:
    ll minimumOperations(vector<int>& nums, vector<int>& target) {
        const int size = nums.size();
        vector<int> df;
        for (int i = 0; i < size; ++i)
            df.push_back(target[i] - nums[i]);
        bool pos = df.back() >= 0;
        int mx;
        ll ans = 0;
        vector<int> tmp;
        while (df.size()) {
            while (df.size() and (df.back() >= 0) == pos) {
                tmp.push_back(abs(df.back()));
                df.pop_back();
            }
            int p = tmp[0];
            int c;
            ans += tmp[0];
            for (int i = 0; i < tmp.size(); ++i) {
                c = tmp[i];
                if (c > p) {
                    ans += c - p;
                }
                p = tmp[i];
            }
            tmp.clear();
            pos = !pos;
        }
        return ans;
    }
};
