typedef long long ll;

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int N = nums.size();
        vector<vector<int>> A,B;
        vector<int> a1,b1;
        for(int i=0;i<N;i++){
            int t = target[i]-nums[i];
            if(t>0){
                if(b1.size())B.push_back(b1);

                b1 = vector<int>();
                a1.push_back(t);
            }else if (t<0){
                if(a1.size())A.push_back(a1);
                a1 = vector<int>();
                b1.push_back(-t);
            }else{
                if(b1.size())B.push_back(b1);
                if(a1.size())A.push_back(a1);

                a1 = vector<int>();
                b1 = vector<int>();
            }
        }

        if(b1.size())B.push_back(b1);
        if(a1.size())A.push_back(a1);

        ll ans = 0,cur;
        for(auto& v:A){
            cur = 0;
            for(auto& a:v){
                cout<<a<<" ";
                if(a>cur){
                    ans+=a-cur;
                }
                cur = a;
            }
            cout<<endl;
        }

        for(auto& v:B){
            cur = 0;
            for(auto& a:v){
                cout<<a<<" ";
                if(a>cur){
                    ans+=a-cur;
                }
                cur = a;
            }
            cout<<endl;
        }

        return ans;
    }
};
