#define nl '\n'
typedef std::vector<int> vi;
typedef std::vector<ll> viL;
typedef std::pair<int, int> pi;
typedef std::pair<ll, ll> pil;
#define all(v) v.begin(), v.end()
#define F first
#define S second
#define YES std::cout << "YES\n"
#define NO std::cout << "NO\n"
#define PB push_back
const int MOD = 1073741824;
const ll BASE = 31;
const int MAXN = 1e5 + 5;
const int MAX = 300005;
const int INF = 1e9;
ll dx[] = {-1, 1, 0, 0};
ll dy[] = {0, 0, -1, 1};

class Solution {
public:
    ll minimumOperations(std::vector<int>& nums, std::vector<int>& target) {
        int n = nums.size();
        std::vector<std::pair<int, char>> diff(n);

        rep(i, 0, n-1) {
            if (target[i] - nums[i] > 0) {
                diff[i] = {target[i] - nums[i], 'P'};
            } else {
                diff[i] = {nums[i] - target[i], 'N'};
            }
        }

        ll val = 0, prev = 0;

        rep(i, 0, n-1) {
            if (i == 0 || diff[i].S == diff[i-1].S) {
                ll aux = diff[i].F - std::abs(prev);
                if (aux > 0) {
                    val += aux;
                }
                prev = diff[i].F;
            } else {
                val += diff[i].F;
                prev = diff[i].F;
            }
        }

        return val;
    }
};
