class Solution {
public:
    long long minNumberOperations(vector<long long> target) {
        vector<long long> stack;
        long long res = 0;
        stack.push_back(target[0]);
        for (int i = 1; i < target.size(); i++) {
            if (target[i] > stack.back()) {
                stack.push_back(target[i]);
                continue;
            } 
            if (target[i] == stack.back())
                continue;
            long long top = stack.back();
            while (!stack.empty() && target[i] < stack.back())
                stack.pop_back();
            res += top - target[i];
            stack.push_back(target[i]);
        }
        if (!stack.empty())
            res += stack.back();
        return res;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<long long> temp;
        long long res=0;
        int f=0;
        if(target[0]>=nums[0])
            f=1;
        temp.push_back(abs(target[0]-nums[0]));
        for(int i=1;i<n;i++)
        {
            int fl=0;
            if(target[i]>=nums[i])
                fl=1;
            if(fl==f)
            {
                temp.push_back(abs(target[i]-nums[i]));
            }
            else
            {
                long long ans=minNumberOperations(temp);
                res+=ans;
                temp.clear();
                f=1-f;
                temp.push_back(abs(target[i]-nums[i]));
            }
        }
        long long ans=minNumberOperations(temp);
        res+=ans;
        return res;
    }
};
