class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        a=[x-y for x,y in zip(target,nums)]
        ans=0
        def solve(a):
            a=[0]+a
            ans=0
            for i in range(len(a)-1):
                if abs(a[i])<abs(a[i+1]):
                    ans+=abs(a[i]-a[i+1])
            return ans
        for g in groupby(a,lambda a: a<0):
            ans+=solve(list(g[1]))
        return ans
