class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        ans = 0
        highest = 0
        larger = True
        for i in range(len(target)):
            diff = target[i] - nums[i]
            if diff == 0:
                ans += highest
                highest = 0
            elif larger ^ (diff > 0):
                ans += highest
                if diff > 0:
                    larger = True
                    highest = diff
                else:
                    larger = False
                    highest = -diff
            else:
                a_diff = abs(diff)
                if a_diff < highest:
                    ans += highest - a_diff
                    highest = a_diff
                else:
                    highest = a_diff
        return ans + highest
