class Solution {
public:
    long long get(vector<int> a){
        int n = a.size();
        if(n==1)return a[0];
        stack<int> s;
        int ans = 0;
        for(int i=0;i<n;i++){
            if(s.size()&&s.top()>=a[i]){
                ans+=s.top()-a[i];
                //cout<<s.size()<<" "<<s.top()<<" "<<a[i]<<endl;
                s.pop();
                // cout<<ans<<endl;
            }

            s.push(a[i]);
            // cout<<a[i]<<" ";
        }
        return ans+(s.top());
    }
    long long minimumOperations(vector<int>& a, vector<int>& t) {
        int ni = a.size();
        vector<int> v(ni);

        for(int i=0;i<ni;i++){
            v[i] = a[i]-t[i];

        }

        int ls = v[0];
        long long ans = 0;
        for(int i=0;i<ni;i++){
            if(ls<0){
                int j = i;
                vector<int> n;
                while(j<ni&&v[j]<0){
                    n.push_back(-v[j]);
                    j++;
                }
                if(n.size())ans+= get(n);
                i=j-1;
                if(i+1<ni)
                ls = v[i+1];
            }
            else{
                int j=i;
                vector<int> p;
                while(j<ni && v[j]>=0){
                    p.push_back(v[j]);
                    j++;
                }
                if(p.size())ans+= get(p);
                if(j<ni)
                ls = v[j];
                i = j-1;
            }


        }
        return ans;
    }
};
