#define print(v)     \
    for (auto a : v) \
        cout << a << " ";
ll __lcm(ll a, ll b)
{
    return (a / __gcd(a, b)) * b;
}
ll mod = 1e9 + 7;
class Solution {
public:
    int solve(vector<int> target) {
        int res = 0;
        stack<int> v;
        for(auto &a:target) a=abs(a); 
        v.push(target[0]);
        for (int i = 1; i < target.size(); i++) {
            if (target[i] > v.top()) {
                v.push(target[i]);
                continue;
            } 
            if (target[i] == v.top())
                continue;

            int top = v.top();
            while (!v.empty() && target[i] < top)
                v.pop();
            res += top - target[i];
            v.push(target[i]);
        }

        if (!v.empty())
            res += v.top();

        return res;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vl diff;
        int n = nums.size();
        fori(i,0,n)
        {
            diff.pb(target[i]-nums[i]);
        }
        // print(diff); cout<<endl;
        vvi v;
        vi curr;
        int idx = 0;
        int pre;
        if(diff[0]>0) pre=1;
        else pre=0;
        while(idx<n)
        {
            if(diff[idx]>0)
            {
                if(pre) curr.pb(diff[idx]);
                else{
                    v.pb(curr); curr={};
                    curr.pb(diff[idx]);
                }
                pre=1;
            }
            else{
                 if(pre==0) curr.pb(diff[idx]);
                else{
                    v.pb(curr); curr={};
                    curr.pb(diff[idx]);
                }
                pre=0;
            }
            idx++;
        }
        if(curr.size()) v.pb(curr);
        ll ans = 0;
        for(auto vv:v)
        {
            ans+=solve(vv);
        }
        return ans;
    }
};
