function minimumOperations(nums: number[], target: number[]): number {
  const pre = {
    op: 0,
    count: 0
  }

  const n = nums.length
  let res = 0

  for (let i = 0; i < n; i++) {
    if (nums[i] > target[i]) {
      if (pre.op !== -1) {
        res += Math.max(nums[i] - target[i] - pre.count, 0)
      } else {
        res += nums[i] - target[i]
      }

      pre.op = 1
      pre.count = nums[i] - target[i]
    } else if (nums[i] < target[i]) {
      if (pre.op !== 1) {
        res += Math.max(-(nums[i] - target[i]) - pre.count, 0)
      } else {
        res += -(nums[i] - target[i])
      }

      pre.op = -1
      pre.count = -(nums[i] - target[i])
    } else {
      pre.op = 0
      pre.count = 0
    }
  }

  return res
};
