class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long> diffs(nums.size());
        for (int i = 0; i < nums.size(); i++) {
            diffs[i] = target[i] - nums[i];
        }
        bool pos = diffs[0] > 0;
        long long cur = diffs[0];
        long long sol = abs(diffs[0]);
        for (int i = 1; i < diffs.size(); i++) {
            if (diffs[i] > 0) {
                if (diffs[i] > max(0ll, diffs[i-1])) {
                    sol += diffs[i] - max(0ll, diffs[i-1]);
                }
            }
            if (diffs[i] < 0) {
                if (diffs[i] < min(0ll, diffs[i-1])) {
                    sol += min(0ll, diffs[i-1]) - diffs[i];
                }
            }
        }
        return sol;
    }
};
