#define ll long long int

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll n = nums.size();
        ll i=0,ans=0;

        while(i<n)
        {
            if(target[i]>=nums[i])
            {
                ll temp = target[i]-nums[i];
                ll prev = temp;
                i++;

                while(i<n && target[i]>=nums[i])
                {
                    ll inc = target[i]-nums[i];
                    ll diff = inc - prev;

                    if(diff>0)
                        temp+=diff;

                    prev = inc;
                    i++;
                }

                ans+=temp;
            }
            else
            {
                ll temp = nums[i]-target[i];
                ll prev = temp;
                i++;

                while(i<n && target[i]<=nums[i])
                {
                    ll inc = nums[i] - target[i];
                    ll diff = inc - prev;

                    if(diff>0)
                        temp+=diff;

                    prev = inc;
                    i++;
                }

                ans+=temp;
            }
        }

        return ans;
    }
};
