class Solution {
    private boolean sameSign(int a, int b) {
        return (a <= 0 && b <= 0) || (a >= 0 && b >= 0);
    }

    public long minimumOperations(int[] nums, int[] target) {
        int i, n = nums.length;
        int[] diff = new int[n];
        long result = 0;
        for(i = 0; i < n; i += 1)
            diff[i] = target[i] - nums[i];
        result = Math.abs(diff[0]);
        for(i = 1; i < n; i += 1) {
            if(sameSign(diff[i], diff[i - 1])) {
                if(Math.abs(diff[i]) > Math.abs(diff[i - 1]))
                    result += Math.abs(diff[i]) - Math.abs(diff[i - 1]);
            }
            else {
                result += Math.abs(diff[i]);
            }
        }
        return result;
    }
}
