int f1[100005],f2[100005];

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        f1[0]=nums[0];
        f2[0]=target[0];
        // nums.push_back(0);target.push_back(0);
        int n=nums.size();
        long long ans=0,neg=0,pos=0;
        for (int i=0;i<n;++i){
            if (i>0){
                f1[i]=nums[i]-nums[i-1],
                f2[i]=target[i]-target[i-1];
            }
            if (f1[i]<f2[i]){
                if (f1[i]+pos>=f2[i]){
                    pos-=f2[i]-f1[i];
                    f1[i]=f2[i];
                }else{
                    int res=f2[i]-f1[i]-pos;
                    ans+=res;
                    pos=0;neg+=res;
                }
            }
            if (f1[i]>f2[i]){
                if (f1[i]-neg<=f2[i]){
                    neg-=f1[i]-f2[i];
                    f1[i]=f2[i];
                    // cout<<neg<<endl;
                }else{
                    int res=f1[i]-f2[i]-neg;
                    ans+=res;
                    pos+=res;neg=0;
                }
            }
        }
        // 4 -3 2 1
        // 4 -3 2 -1
        return ans;
    }
};
