class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        for (int i = 0; i < nums.size(); ++i) {
            nums[i] -= target[i];
        }
        // 找连续， 大于0和小于0分开讨论
        long long ans = 0;
        // 大于0
        int last = 0;
        for (int i = 0; i < nums.size(); ++i) {
            if (nums[i] > 0 && nums[i] - last > 0) {
                ans += nums[i] - last;
            }
            if (nums[i] >= 0) last = nums[i];
            else last = 0;
        }
        last = 0;
        for (int i = 0; i < nums.size(); ++i) {
            if (nums[i] < 0 && nums[i] - last < 0) {
                ans -= (nums[i] - last);
            }
            if (nums[i] <= 0) last = nums[i];
            else last = 0;
        }
        return ans;
    }
};
