class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        int last = 0;
        for(int i = 0; i < n; ++i) {
            int tmd = target[i] - nums[i];
            if(last == 0 || tmd == 0) ans += abs(tmd);
            else if(last > 0 && tmd > 0) {
                if(tmd > last) ans += tmd - last;
            } else if(last < 0 && tmd < 0) {
                if(last > tmd) ans += last - tmd; 
            } else {
                if(tmd >= 0) ans += tmd;
                else ans += -tmd;
            }
            last = tmd;
        }
        return ans;
    }
};
