class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        for (int i = 0; i < nums.size(); ++i) {
            nums[i] -= target[i];
        }
        long long ans = 0;
        vector<vector<int>> p;
        vector<int> v, vv;
        for (int i = 0; i < nums.size(); ++i) {
            if (nums[i] < 0) {
                v.emplace_back(nums[i]);
                if (!vv.empty()) p.emplace_back(vv);
                vv.clear();
            }
            else {
                if (!v.empty()) p.emplace_back(v);
                v.clear();
                vv.emplace_back(nums[i]);
            }
        }
        if (!v.empty()) p.emplace_back(v);
        if (!vv.empty()) p.emplace_back(vv);
        for (int i = 0; i < p.size(); ++i) {
            int t1 = abs(p[i][0]);
            int t2;
            int cur = t1;
            ans += t1;
            for (int j = 1; j < p[i].size(); ++j) {
                t2 = abs(p[i][j]);
                ans += max(0, t2 - cur);
                // if (t2 >= t1)  {
                    // cur = t2;
                // } else {
                    cur = t2;
                // }
                t1 = t2;
            }
        }
        return ans;
    }
};
