class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> up(n), down(n);
        for (int i = 0; i < n; i++) {
            int diff = nums[i] - target[i];
            if (diff > 0) up[i] = diff;
            else if (diff < 0) down[i] = -diff;
        }
        long long ans = up[0] + down[0];
        for (int i = 1; i < n; i++) {
            ans += max(up[i] - up[i - 1], (long long)0);
            ans += max(down[i] - down[i - 1], (long long)0);
        }

        return ans;
    }
};
