class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        target_nums = [target[i]-nums[i] for i in range(n)]
        print(target_nums)
        res = 0
        res += abs(target_nums[0])
        for i in range(1, n):
            if target_nums[i-1] == 0:
                res += abs(target_nums[i])
            if target_nums[i-1] * target_nums[i] > 0: # 同符号
                if abs(target_nums[i]) > abs(target_nums[i-1]):
                    res += abs(target_nums[i]) - abs(target_nums[i-1])
            elif target_nums[i-1] * target_nums[i] < 0:
                res += abs(target_nums[i])
        return res
