class Solution {
public:
    long long get_ans(vector<int> &v){

        for(auto &i:v){
            i = abs(i);
        }
        long long ans = v[0];

        for(int i=1;i<v.size();i++){
            if(v[i]>v[i-1]){
                ans += v[i]-v[i-1];
            }
        }
        return ans;
    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> v;
        int n = nums.size();
        for(int i=0;i<n;i++){
            v.push_back(target[i] - nums[i]);
        }

        vector<int> temp;
        long long ans = 0;
        temp.push_back(v[0]);
        for(int i=1;i<n;i++){
            if((v[i]< 0) != (v[i-1]< 0)){
                ans += get_ans(temp);
                temp.clear();
            }
            temp.push_back(v[i]);
        }
        ans += get_ans(temp);
        return ans;
    }
};
