class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long ans = 0;
        long long last_inc = 0;

        for(int i=0; i<n; i++){
            long long dif = target[i]-nums[i];
           if(last_inc<0){
                if(dif<0){
                    if(dif>=last_inc){
                        last_inc = dif;
                    }
                    else{
                        ans += (last_inc-dif);
                        last_inc = dif;
                    }

                }
                else{
                    last_inc = dif;
                    ans += last_inc;
                }

           }
           else{
            if(dif>0){
                if(dif>=last_inc){
                    ans+=(dif-last_inc);
                    last_inc = dif;
                }
                else{
                    last_inc = dif;
                }
            }
            else{
                last_inc = dif;
                ans += abs(dif);
            }
           }

        }

        return ans;
    }
};
