class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        def todiff(xs):
            ans = [xs[0]]
            for i in range(1, len(xs)):
                ans.append(xs[i] - xs[i - 1])
            ans.append(-xs[-1])
            return ans
        n1, n2 = todiff(nums), todiff(target)
        print(f'n1={n1}, n2={n2}')
        ans2 = 0
        for i in range(len(n1)):
            ans2 += abs(n1[i] - n2[i])
        #print(f'ans2={ans2}')            
        return ans2 // 2
