class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int l = nums.length , change = 0;
        long count = 0;
        for(int i = 0;i<l;i++){
            int t = target[i] - nums[i];
            if(change >= 0 && t >= 0){
                if(t <= change){
                    change = t;
                }
                else{
                    count += (t - change);
                    change = t;
                }
            }
            else if(change >= 0 && t < 0){
                count += (Math.abs(t));
                change = t;
            }
            else if(change <= 0 && t < 0){
                if(t >= change){
                    change = t;
                }
                else{
                    count += (change - t);
                    change = t;
                }
            }
            else{
                count += t;
                change = t;
            }
        }
        return count;
    }
}
