class Solution {
public:
    const static int MN = 1e5+8;
    long long sven [MN];
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for (int i = 0; i < n; i++) {
            sven[i] = target[i] - nums[i];
        }
        return solve(0, n-1);
    }

    long long solve(int l, int r) {
        if (l == r) return abs(sven[l]);
        int m = l + (r - l)/2;

        long long X = solve(l, m);
        long long Y = solve(m+1, r);

        long long A = sven[m];
        long long B = sven[m+1];
        long long C = 0;
        if ((A >= 0 && B >= 0) || (A <= 0 && B <= 0)) {
            C = min(abs(A),abs(B));
        } else {
            C = 0;
        }

        return X + Y - C;
    }
};
