#define ll long long

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll n = nums.size(), ans = 0;
        vector<ll> v(n, 0), op(n, 0);
        for(int i=0; i<n; i++)
            v[i] = target[i] - nums[i];
        op[0] = abs(v[0]);

        for(int i=1; i<n; i++)  {
            if(v[i] == 0)   continue;
            if(v[i-1] == 0) {
                op[i] = abs(v[i]);
            }
            else if(v[i-1] < 0 && v[i] < 0)  {
                if(v[i-1] < v[i])   
                    op[i] = 0;
                else
                    op[i] = abs(v[i] - v[i-1]);
            }
            else if(v[i-1] > 0 && v[i] > 0)  {
                if(v[i-1] > v[i])
                    op[i] = 0;
                else
                    op[i] = v[i] - v[i-1];
            }
            else    {
                op[i] = abs(v[i]);
            }

        }

        for(int i=0; i<n; i++)
            ans += op[i];
        return ans;
    }
};
