#define ll long long int
class Solution {
public:
    ll minimumOperations(vector<int>& nums, vector<int>& target) {
        ll ans = 0, inc = 0, dec = 0;
        int n = nums.size();
        vector<ll>diff(n, 0);

        for(int i=0; i<n; i++){
            diff[i] = target[i] - nums[i];
        }

        if(diff[0] < 0){
            dec = diff[0];
        }
        else{
            inc = diff[0];
        }

        ans += abs(dec) + inc;

        for(int i=1; i<n; i++){
            if(diff[i-1] == diff[i] || diff[i] == 0) {
                if(diff[i] == 0){
                    inc = 0, dec =0;
                }
                continue;
            }
            if(diff[i] < 0){
                inc = 0;
                if(abs(diff[i]) <= abs(dec)){
                    dec = diff[i];
                }
                else{
                    ans += abs(diff[i]) - abs(dec);
                    dec = diff[i];
                }
            }
            else{
                dec = 0;
                if(diff[i] <= inc){
                    inc = diff[i];
                }
                else{
                    ans += diff[i] - inc;
                    inc = diff[i];
                }
            }
        }

        return ans;
    }
};
