class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> diff;
        diff.assign(nums.size(), 0);
        for (int i = 0; i < diff.size(); ++i) {
            diff[i] = target[i] - nums[i];
        }

        int c = 0;
        long long sum = 0;
        for (int i = 0; i < diff.size(); ++i) {
            if (c <= 0 && diff[i] > 0 || c >= 0 && diff[i] < 0) {
                sum += abs(diff[i]);
                c = diff[i];
            } else {
                if (abs(diff[i]) >= abs(c)) {
                    sum += (abs(diff[i]) - abs(c));
                    c = diff[i];
                } else {
                    sum += 0;
                    c = diff[i];
                }
            }
        }
        return sum;
    }
};
