def run(target):
    n = len(target)
    dp = [0] * n
    for idx, val in enumerate(target):
        if not idx:
            dp[idx] = val
        elif target[idx] <= target[idx - 1]:
            dp[idx] = dp[idx - 1]
        else:
            dp[idx] = dp[idx - 1] + target[idx] - target[idx-  1]

    return dp[-1]

class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        dp = []
        for i, j in zip(nums, target):
            dp.append(j - i)
        cost = 0
        lo = 0
        n = len(dp)
        while lo < n:
            while lo < n and dp[lo] == 0:
                lo += 1
            hi = lo
            curr = []
            while hi < n and ((dp[hi] > 0) == (dp[lo] > 0)):
                curr.append(abs(dp[hi]))
                hi += 1
            lo = hi
            while lo < n and dp[lo] == 0:
                lo += 1
            if curr:
                cost += run(curr)
        return cost
