class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long ans = 0;
        int curr = 0;
        for(int i = 0; i < n; i++)
        {
            int req = target[i] - nums[i];
            if((curr >= 0 && req >= 0) || (curr < 0 && req < 0))
            {
                if(abs(curr) < abs(req)) ans += abs(req) - abs(curr);
                curr = req;
            }
            else
            {
                ans += abs(req);
                curr = req;
            }
        }

        return ans;
    }
};
