class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        for i in range(len(nums)):
            target[i]-=nums[i]
        c=[]
        for i in range(len(target)):
            if i==0:
                c.append(target[i])
            else:
                c.append(target[i]-target[i-1])
        # print(c)
        x1=0
        x2=0
        for x in c:
            if x>0:
                x1+=x
            else:
                x2-=x
        return max(x1,x2)
