class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int>diffmore(n,0);
        vector<int>diffless(n,0);
        for(int i=0;i<n;++i){
            if(nums[i]>target[i]){
                diffmore[i]=nums[i]-target[i];
            }
            else{
                diffless[i]=target[i]-nums[i];
            }
        }
        long long ans=0;
        vector<int>up;
        up.push_back(0);
        int prev=0;
        int mx=0;
        int prev1=0;
        for(int i=0;i<n;++i){
            // cout<<diffmore[i]<<" ";
            if(diffmore[i]>mx){
                if(prev1>0){
                    up.push_back(mx);
                }
                mx=diffmore[i];
                prev=1;
                prev1=0;
            }
            else if(diffmore[i]<mx){
                if(prev>0){
                    up.push_back(mx);
                }
                mx=diffmore[i];
                prev1=1;
                prev=0;
            }
        }
        if(prev>0){
            up.push_back(mx);
        }
        if(prev1>0){
            up.push_back(mx);
        }
        // cout<<up.size()<<endl;
        for(int i=1;i<up.size();i+=2){
            ans+=abs(up[i]-up[i-1]);
        }
        // cout<<ans<<" ";
        vector<int>down;
        down.push_back(0);
        prev=0;
        mx=0;
        prev1=0;
        for(int i=0;i<n;++i){
            // cout<<diffless[i]<<" ";
            if(diffless[i]>mx){
                if(prev1>0){
                    down.push_back(mx);
                }
                mx=diffless[i];
                prev=1;
                prev1=0;
            }
            else if(diffless[i]<mx){
                if(prev>0){
                    down.push_back(mx);
                }
                mx=diffless[i];
                prev1=1;
                prev=0;
            }
        }
        if(prev>0){
            down.push_back(mx);
        }
        if(prev1>0){
            down.push_back(mx);
        }
        // cout<<down.size()<<endl;
        for(int i=1;i<down.size();i+=2){
            ans+=abs(down[i]-down[i-1]);
        }
        return ans;
    }
};
