class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long int ans = 0;
        int n = nums.size();
        vector<int>v(n);
        int c = 0;

        for(int i=0;i<n;i++)
        {
            v[i] = target[i] - nums[i];
        }

        for(int i=0;i<n;i++)
        {
            if(i == 0)
            {
                ans += abs(v[i]);
                c = v[i];
            }
            else if(v[i] == c)
                continue;
            else if(v[i] == 0)
            {
                c = 0;
                continue;
            }
            else
            {
                if(v[i] < 0)
                {
                    if(c < 0)
                    {
                        if(v[i] < c)
                        ans += abs(abs(v[i]) - abs(c));
                        c = v[i];
                    }
                    else
                    {
                        ans += abs(v[i]);
                        c = v[i];
                    }
                }
                else
                {
                    if(c < 0)
                    {
                        ans += v[i];
                        c = v[i];
                    }
                    else
                    {
                        if(v[i] > c)
                        ans += abs(v[i] - c);
                        c = v[i];
                    }
                }
            }
        }

        return ans;

    }
};
