class Solution {
public:
    long long func(vector<int>& diff){
        long long res = diff[0];
        int n=diff.size();
        for(int i=1;i<n;i++){
            res = res + max(diff[i]-diff[i-1],0);
        }
        return res;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> diff(nums.size());
        long long ans=0;
        int n=nums.size();
        for(int i=0;i<n;i++){
            diff[i] = nums[i]-target[i];
        }
        for(int i=0;i<n;){
            vector<int> tmp;
            if(diff[i]==0){
                i++;
                continue;
            }
            bool pos = diff[i]>0;
            if(pos){
                while(i<n && diff[i]>=0){
                    tmp.push_back(diff[i]);
                    i++;
                }
            }else{
                while(i<n && diff[i]<=0){
                    tmp.push_back(-1*diff[i]);
                    i++;
                }
            }
            if(tmp.size()>0)
                ans = ans + func(tmp); 
        }
        return ans;
    }
};
