class Solution {
public:
long long minimumOperations(vector<int>& nums, vector<int>& target) {
    long long res=0;
    long long t=0;
    for(int i=0;i<nums.size();++i){
        long long tmp=target[i]-nums[i];
        if(tmp>=0 && t>=0){
            if(tmp>t) {
                res+=tmp-t;
            }

            t=tmp;
        } else if(tmp<0 && t<0){
            if(tmp<t)
            res+=abs(tmp)-abs(t);
            t=tmp;
        } else{
            res+=abs(tmp);
            t=tmp;
        }


    }
    return res;
}


};
