class Solution
{
  public:
    long long minimumOperations(vector<int> &nums, vector<int> &target)
    {
        int n = nums.size();
        vector<int> a(n);
        for (int i = 0; i < n; i++)
            a[i] = target[i] - nums[i];
        for(int i=n-1;i>=1;i--) a[i]=a[i]-a[i-1];
        for(int i=0;i<n;i++) cout<<a[i]<<' ';
        long long ans=0;
        long long ans2=0;
        for(auto x:a) if(x>0) ans+=x;else ans2-=x;
        return max(ans,ans2);
    }
};
