class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        for(int i=0;i<n;++i)
        {
            nums[i]-=target[i];
            cout<<nums[i]<<" \n"[i==n-1];
        }
        vector<long long>d(n+1);
        nums.push_back(0LL);
        d[0]=nums[0];cout<<d[0]<<" ";
        long long ans=(d[0]>0?d[0]:0);
        for(int i=1;i<=n;++i)
        {
            d[i]=nums[i]-nums[i-1];
            cout<<d[i]<<" ";
            if(d[i]>0)ans+=d[i];
        }
        return ans;
    }
};
