class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        int arr[] = new int[n];
        for(int i = 0; i < n; ++i) arr[i] = -nums[i] + target[i];
        long ans = 0;
        long sum = 0;
        for(int i = 0; i < n; ++i) {
            if(arr[i] > 0) {
                if(sum > 0) {
                    if(sum < arr[i]) {
                        ans += arr[i] - sum;
                    }
                             sum = arr[i];

                }
                else {
                    ans += arr[i];
                    sum = arr[i];
                }
            }
            else if(arr[i] < 0) {
                if(sum > 0) {
                    ans += -arr[i];
                }
                else {
                    if(sum > arr[i]) {
                        ans += sum - arr[i];
                    }
                }
                                    sum = arr[i];

            }
            else {
                sum = 0;
            }
        }
        return ans;
    }
}
