class Solution {
    public long minimumOperations(int[] nums, int[] target) {

        int cnt1 = 0;
        int cnt2 = 0;
        long res = 0;
        int n = nums.length;
        for (int i = 0; i < n; i++) {
            int x = target[i] - nums[i];
            if (x > 0) {
                if (cnt1 > 0) {
                    if (x > cnt1) {
                        res += x - cnt1;
                        cnt1 = x;
                    }
                    else {
                        cnt1 = x;
                    }
                } else {
                    cnt1 = x;
                    cnt2 = 0;
                    res += x;
                }
            } else {
                if (cnt2 > 0) {
                    if (-x > cnt2) {
                        res += -x - cnt2;
                        cnt2 = -x;
                    } else {
                        cnt2 = -x;
                    }
                } else {
                    cnt2 = -x;
                    cnt1 = 0;
                    res += -x;
                }
            }

        }
        return res;
    }
}
