class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        d1 = [0] * (n+1)
        d2 = [0] * (n+1)
        for i in range(n):
            d1[i] = nums[i] if i == 0 else nums[i] - nums[i-1]
            d2[i] = target[i] if i == 0 else target[i] - target[i-1]
        d = [a-b for a, b in zip(d1, d2)]
        p, m = 0, 0
        ans = 0
        for x in d:
            if x == 0:
                continue
            if x > 0:
                if m <= x:
                    x -= m
                    m = 0
                else:
                    m -= x
                    x = 0
                ans += x
                p += x
            else:
                if p <= -x:
                    x += p
                    p = 0
                else:
                    p += x
                    x = 0
                ans -= x
                m -= x
        # ans += abs(p) + abs(m)
        return ans
